let newMsg = [];

for (let i in msg.payload.pathToChange) {

    let strPath = msg.payload.pathToChange[i];

    // Early Exit
    if (strPath == undefined) {return};
    if (!strPath.startsWith("fairlight.")) {return};
    if (!strPath.endsWith(".levels")) {return};

    // Master
    if (strPath === "fairlight.master.levels") {
        let leftLevel = msg.payload.state.fairlight.master.levels.leftLevel;
        let rightLevel = msg.payload.state.fairlight.master.levels.rightLevel;
        let maxLevel = Math.max(leftLevel, rightLevel);
        let payload = maxLevel / 100;
        let channel = "Master";
        let topic = strPath;
        newMsg.push({ payload: payload, channel: channel, topic: topic});
    }

    // Inputs
    if (strPath.startsWith("fairlight.inputs.") && strPath.endsWith(".levels")) {
        let pathPart = strPath.split(".");
        let inputPart = msg.payload.state.fairlight.inputs[pathPart[2]];
        let sourcePart = inputPart.sources[pathPart[4]];
        let leftLevel = sourcePart.levels.leftLevel;
        let rightLevel = sourcePart.levels.rightLevel;
        let maxLevel = Math.max(leftLevel, rightLevel);
        let payload = maxLevel / 100;
        let channel = pathPart[2];
        let topic = strPath;
        newMsg.push({ payload: payload, channel: channel, topic: topic });
    }

}

return [newMsg];

