function linearInterpolation(x, x0, x1, y0, y1) {
    // Ensure x is within the range [x0, x1]
    //if (x < x0 || x > x1) {
    //    throw new Error('The value of x is outside the range of interpolation');
    //}
    // Calculate the interpolated value
    const y = y0 + ((y1 - y0) / (x1 - x0)) * (x - x0);
    return y;
}

