let inputSource = msg.payload.input.inputSource
let sql = "";

switch (msg.topic) {
    case "insert":
        sql = "INSERT INTO device_input_rltn (deviceId, inputId, inputSource) \n" +
              "   SELECT '" + deviceId + "' AS deviceId, " + inputId + " as inputId, '" + inputSource + "' as inputSource \n" +
              "   WHERE NOT EXISTS (SELECT 1 FROM device_input_rltn WHERE deviceId = '"+deviceId+"' AND inputId = "+inputId+" AND inputSource = '"+inputSource+"')"
        break;
    case "delete":
        sql = "DELETE FROM device_input_rltn \n" +
              "   WHERE 1 = 1 \n" +
              "     AND deviceId = '" + deviceId + "'\n" +
              "     AND inputId = '" + inputId + "'\n" +
              "     AND inputSource = '" + inputSource + "'\n"
    default:
        break;
}

delete msg.payload;
delete msg.topic;
msg.topic = sql;

return msg;

